const toggleMenu=document.querySelector(".toggle"),menu=document.querySelector(".menu__list");function toggleFunction(){this&&menu.classList.toggle("menu__list-is-active")}toggleMenu.addEventListener("click",toggleFunction);const selectedSructure=document.querySelectorAll(".active"),targetItem=document.querySelectorAll(".item");function selectContent(){selectedSructure.forEach(e=>{this.dataset.title===e.dataset.content&&e.classList.toggle("active-selected")})}targetItem.forEach(e=>{e.addEventListener("click",selectContent)});