const toggleMenu=document.querySelector(".toggle"),menu=document.querySelector(".menu__list");function toggleFunction(){this&&menu.classList.toggle("menu__list-is-active")}toggleMenu.addEventListener("click",toggleFunction);const selectedSructure=document.querySelectorAll(".active"),targetItem=document.querySelectorAll(".item");function selectContent(){selectedSructure.forEach(e=>{this.dataset.title===e.dataset.content&&e.classList.toggle("active-selected")})}targetItem.forEach(e=>{e.addEventListener("click",selectContent)});const sliderBlock=document.querySelectorAll(".slider-block");let count=0;function activeBlock(){sliderBlock.forEach(e=>{e.classList.remove("slider-block-active")}),sliderBlock[count].classList.add("slider-block-active")}document.querySelector(".btn-right").addEventListener("click",e=>{count+1<sliderBlock.length?count++:count=0,activeBlock(),sliderBlock[count].style.cssText="animation: 1s ease-in-out 0s alternate slider-right"}),document.querySelector(".btn-left").addEventListener("click",e=>{0<count?count--:count=sliderBlock.length-1,activeBlock(),sliderBlock[count].style.cssText="animation: 1s ease-in-out 0s alternate slider-left"});const shopExamples=document.querySelectorAll(".shop");function changeShopExamples(e){e.target;shopExamples.forEach(e=>{e.classList.toggle("unvisible-mob"),e.style.cssText="transition: 0.7s"})}document.querySelector(".mob-button").addEventListener("click",changeShopExamples);