const sliderBlock=document.querySelectorAll(".slider-block");let count=0;function activeBlock(){sliderBlock.forEach(e=>{e.classList.remove("slider-block-active")}),sliderBlock[count].classList.add("slider-block-active")}document.querySelector(".btn-right").addEventListener("click",e=>{count+1<sliderBlock.length?count++:count=0,activeBlock(),sliderBlock[count].style.cssText="animation: 1s ease-in-out 0s alternate slider-right"}),document.querySelector(".btn-left").addEventListener("click",e=>{0<count?count--:count=sliderBlock.length-1,activeBlock(),sliderBlock[count].style.cssText="animation: 1s ease-in-out 0s alternate slider-left"});